import React, { FunctionComponent, useEffect, useState } from 'react';
import Pokemon from '../models/pokemon';
import './pokemon-card.css';
import formatType  from '../helpers/format-type';
import formaDate from '../helpers/format-date';
import { useNavigate } from 'react-router-dom';
type Props = {
  pokemon: Pokemon,
  borderColor?:string 
};
  
const PokemonCard: FunctionComponent<Props> = ({pokemon, borderColor = '#009688'}) => {
 
  const [color, setColor] = useState<string>();
  const navigate = useNavigate();


  const handleClick = (event: React.MouseEvent) => {
    let starterContainer = (event.target as HTMLElement).closest('.starter-container');

    if (starterContainer && starterContainer.classList.contains('active')) {
      starterContainer.classList.remove('active');
    } else {
      const activeStarterContainer = document.querySelector('.starter-container.active');
      activeStarterContainer?.classList.remove('active');
      
      if (starterContainer) {
        starterContainer.classList.add('active');
      }
    }
  };

  useEffect(() => {
    const starters = document.querySelector('.starters');
    starters?.addEventListener('click', handleClick as unknown as EventListener);

    return () => {
      starters?.removeEventListener('click', handleClick as unknown as EventListener);
    };
  }, []);

 
  const showBorder = () => {
   setColor(borderColor);
   console.log('in');
   console.log(color);

  }

  const hideBorder = () => {
    setColor('#009688');
    console.log('leave');
    console.log(color);

  }

  const goToPokemon = () => {
    // Attendre 2 secondes avant de naviguer vers la nouvelle page
    setTimeout(() => {
      navigate(`/pokemon/${pokemon.id}`);
    }, 2000); // 2000 millisecondes (2 secondes)
  };


 

  return (
      <div className="col-6 col-md-2 col-lg-3" onClick={goToPokemon} onMouseEnter={showBorder} onMouseLeave={hideBorder} style={{borderColor:color}} >
       
         <div className="starters">
            <div className="starter-container">
               
                <div className="pokeball">
                    <div className="top-part"></div>
                    <div className="bottom-part"></div>
                    <div className="middle-part"></div>
                    <div className="middle-circle"></div>
                    <button className="pokeball-button">
                    <img
  src={pokemon.name ? `../img/model/${pokemon.name}.gif` : pokemon.picture}
  alt={pokemon.name}
  className="img-pokemon starter-image"
/>   
                    </button>
                </div>
                <h2 className="pokemon-name">Bulbasaur</h2>
            </div>

        </div>
      </div>
  );
}
  
export default PokemonCard;
