import React, { FunctionComponent, useState } from 'react';
import Pokemon from '../models/pokemon';
import './pokemon-card.css';
import formatType  from '../helpers/format-type';
import formaDate from '../helpers/format-date';
import { useHistory } from 'react-router-dom';
type Props = {
  pokemon: Pokemon,
  borderColor?:string 
};
  
const PokemonCard: FunctionComponent<Props> = ({pokemon, borderColor = '#009688'}) => {
 
  const [color, setColor] = useState<string>();
  const history = useHistory( )
 
  const showBorder = () => {
   setColor(borderColor);
   console.log('in');
   console.log(color);

  }

  const goToPokemon = (id:number) => {
    setColor(borderColor);
    console.log('in');
    console.log(color);
 
   }

  const hideBorder = () => {
    setColor('#009688');
    console.log('leave');
    console.log(color);

  }

  const handleClick: React.MouseEventHandler<HTMLDivElement> = (event) => {
    // Votre logique de gestion de clic ici
    // Par exemple, naviguer vers une nouvelle page
    history.push('/nouvelle-page');
  };
  

  return (
      <div className="col-12 col-md-6 col-lg-3" onClick={goToPokemon(pokemon.id)} onMouseEnter={showBorder} onMouseLeave={hideBorder} style={{borderColor:color}} >
          <div className="card card-pokemon my-4 px-2">
          <img src={pokemon.picture} alt={pokemon.name} className="card-img-top"/>
          <h5 className="card-title">{pokemon.name}</h5>
         <p className="card-text">
           {pokemon.types.map(type => (
                <span className={formatType(type)+" capsule"} key={type}>{type} </span>
            ))}</p>
            <p><small>{formaDate(pokemon.created)}</small></p>
            <div className="pokeball"></div>
    </div>
    </div>
  );
}
  
export default PokemonCard;
