import React, { FunctionComponent, useState } from 'react';
import Pokemon from '../models/pokemon';
import './pokemon-card.css';
import formatType  from '../helpers/format-type';
import formaDate from '../helpers/format-date';
import { useNavigate } from 'react-router-dom';
type Props = {
  pokemon: Pokemon,
  borderColor?:string 
};
  
const PokemonCard: FunctionComponent<Props> = ({pokemon, borderColor = '#009688'}) => {
 
  const [color, setColor] = useState<string>();
  const navigate = useNavigate();

 
  const showBorder = () => {
   setColor(borderColor);
   console.log('in');
   console.log(color);

  }

  const hideBorder = () => {
    setColor('#009688');
    console.log('leave');
    console.log(color);

  }

  const goToPokemon = () => {
    navigate(`/pokemon/${pokemon.id}`);
  };


  return (
      <div className="col-6 col-md-2 col-lg-2" onClick={goToPokemon} onMouseEnter={showBorder} onMouseLeave={hideBorder} style={{borderColor:color}} >
          <div className="card1 my-3">
          <img
  src={pokemon.name ? `../img/model/${pokemon.name}.gif` : pokemon.picture}
  alt={pokemon.name}
  className="img-pokemon"
/>         <h5 className="card-title">{pokemon.name}</h5>
         <p className="card-text">
           {pokemon.types.map(type => (
                <span className={formatType(type)+" capsule"} key={type}>{type} </span>
            ))}  
          </p>
         </div>
         <div className="starters">
            <div className="starter-container">
                <svg className="arrow" viewBox="0 0 142 167" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M97 2H44V89.75H4.31604L71 164L137.684 89.75H97V2Z" fill="#4098D7"/>
                    <path d="M97 2H44V89.75H4.31604L71 164L137.684 89.75H97V2Z" fill="url(#paint0_linear_1_18)" fill-opacity="0.2"/>
                    <path fill-rule="evenodd" clip-rule="evenodd" d="M42.5 0.5H98.5V88.25H141.047L71 166.245L0.952759 88.25H42.5V0.5ZM45.5 3.5V91.25H7.67934L71 161.755L134.321 91.25H95.5V3.5H45.5Z" fill="#F8EFED"/>
                    <defs>
                    <linearGradient id="paint0_linear_1_18" x1="4" y1="90" x2="138" y2="90" gradientUnits="userSpaceOnUse">
                    <stop stop-color="white"/>
                    <stop offset="0.397941" stop-color="white"/>
                    <stop offset="0.555282"/>
                    <stop offset="1"/>
                    </linearGradient>
                    </defs>
                </svg>
                <div className="pokeball">
                    <div className="top-part"></div>
                    <div className="bottom-part"></div>
                    <div className="middle-part"></div>
                    <div className="middle-circle"></div>
                    <button className="pokeball-button">
                        <img src="https://assets.pokemon.com/assets/cms2/img/pokedex/full/001.png" alt="bulbasaur" className="starter-image"/>
                    </button>
                </div>
                <h2 className="pokemon-name">Bulbasaur</h2>
            </div>

        </div>
      </div>
  );
}
  
export default PokemonCard;
