import React, { FunctionComponent, useState } from 'react';
import Pokemon from '../models/pokemon';
import './pokemon-card.css';
import formatType  from '../helpers/format-type';
type Props = {
  pokemon: Pokemon,
  borderColor?:string 
};
  
const PokemonCard: FunctionComponent<Props> = ({pokemon, borderColor = '#009688'}) => {
 
  const [color, setColor] = useState<string>();
 
  const showBorder = () => {
   setColor(borderColor);
   console.log('in');
  }


  const hideBorder = () => {
    setColor('#009688');
    console.log('leave');
  }
  
  const formaDate = (date:Date) =>{
    return `${date.getDay()}/${date.getMonth()+1}/${date.getFullYear()}`
  }

 
  return (
      <div className="col-12 col-md-6 col-lg-4" onMouseEnter={showBorder} onMouseLeave={hideBorder} style={{borderColor:color}} >
          <div className="card">
          <img src={pokemon.picture} alt={pokemon.name} className="card-img-top"/>

      <h5 className="card-title">{pokemon.name}</h5>
      <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
      <p className="card-text"> {pokemon.types.map(type => (
                <span className={formatType(type)} key={type}>{type} <br /></span>
            ))}</p>
                    <p><small>{formaDate(pokemon.created)}</small></p>
            <p>{pokemon.types.map(type => (
                <span className={formatType(type)} key={type}>{type} <br /></span>
            ))}</p>
    </div>
        <div className="card horizontal" >
        <div className="card-image"> 
        </div>
        <div className="card-stacked">
          <div className="card-content">
            <p>{pokemon.name}</p>
    
          </div>
        </div>
      </div> 
    </div>
   
  );
}
  
export default PokemonCard;
